import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";

export function FetchContatos() {

    const [data, setData] = useState([]);
    const [updateContatos, setUpdateContatos]= useState(false);

    useEffect(() => {
        const fetchContacts = async () => {
          try {
            const response = await fetch("http://localhost:3001/contatos");
            const data = await response.json();
            setData(data);
          } catch (err) {
            console.error('Api Request contatos Error: ', err);
          }
        };
    
        fetchContacts();
      }, [updateContatos])

    const deleteContact = async (id) => {
        try {
        await fetch(`http://localhost:3000/contatos/${id}`, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json',
            },
          });

          setUpdateContatos(prev => !prev);
        } catch (err) {
      console.error('Api Request Delete Error: ', err);
    }   
      }

  


function handleClick(id){
    const apagarConfirmacao = window.confirm("¿Tem certeza de que deseja apagar este contato?");
    if (apagarConfirmacao === true){
        deleteContact(id);
        
    }
   
}

function filterAndUpdateData(filter){

  return data.filter(contato =>
    Object.values(contato).some(value =>
        value.toLowerCase().includes(filter.toLowerCase())
    )
);
}

function handleOnChange(event){
  const entries = event.target.value;
  const filteredData = filterAndUpdateData(entries);

  setData(filteredData);
}

  return  (
    <input type="text" onChange={handleOnChange}/>,
    
    data.map(contato => (
    <li key={contato.id}>
        <h3>{contato.nome}</h3>
        <p>{contato.telefone}</p>
        <p>{contato.email}</p>
        <button><Link to="/editarcontato">Editar</Link></button> <button value={contato.id} onClick={() => handleClick(contato.id)}>Apagar</button>
    </li>
))
)
}
