import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import CadastrarContato from './CadastrarContato'; // Make sure to import your component
import SearchInput from './SearchInput'; // Import SearchInput
import Contatos from './Contatos'; // Import Contatos

const App = () => {
  const location = useLocation();
  const showButton = location.pathname !== "/+";
  const showContatos = location.pathname !== "/+";

  return (
    <>
      <SearchInput />
      {showButton && (
        <button>
          <Link to="/+">+</Link>
        </button>
      )}

      <Routes>
        <Route path="/+" element={<CadastrarContato />} />
      </Routes>

      {showContatos && <Contatos />}
    </>
  );
}

const AppWrapper = () => {
  return (
    <Router>
      <App />
    </Router>
  );
}

export default AppWrapper;
