import React, { useState, useEffect } from 'react';
import ApagarContato from './ApagarContato';

export function FetchContatos({refresh}) {

    const [data, setData] = useState([]);

    useEffect(() => {
      fetch("http://localhost:3000/contatos")
        .then((response) => 
          response.json())
        .then((data) => setData(data))
        .catch((err)=>
      console.error('Api Request contatos Error: ', err));
  
    }, [refresh]);

  return data.map(contato => (
    <li key={contato.id}>
        <h3>{contato.nome}</h3>
        <p>{contato.telefone}</p>
        <p>{contato.email}</p>
        <button>Editar</button> <ApagarContato contactId={contato.id}/>
    </li>
));
}
