import React, { useState, useEffect } from 'react';
import ApagarContato from './ApagarContato';

export function FetchContatos() {

    const [data, setData] = useState([]);
    const [updateContatos, setUpdateContatos]= useState(false);

    useEffect(() => {
      fetch("http://localhost:3000/contatos")
        .then((response) => 
          response.json())
        .then((data) => setData(data))
        .catch((err)=>
      console.error('Api Request contatos Error: ', err));
  
    }, [updateContatos]);

    const deleteContact = async (id) => {
        try {
        fetch(`http://localhost:3000/contatos/${id}`, {
            method: 'DELETE',
            headers: {
              'Content-Type': 'application/json',
            },
          });

          setUpdateContatos(prev => !prev);
        } catch (err) {
      console.error('Api Request Delete Error: ', err);
    }   
      }

function handleClick(id){
    const apagarConfirmacao = window.confirm("¿Tem certeza de que deseja apagar este contato?");
    if (apagarConfirmacao === true){
        deleteContact(id);
        
    }
   
}

  return data.map(contato => (
    <li key={contato.id}>
        <h3>{contato.nome}</h3>
        <p>{contato.telefone}</p>
        <p>{contato.email}</p>
        <button>Editar</button> <button value={contato.id} onClick={() => handleClick(contato.id)}>Apagar</button>
    </li>
));
}
