import React from "react";
import { Link } from "react-router-dom";

const EditarContatoApp = (data) => {
  return (
    <div>
      EditarContato:
      <div>
        <Link to="/">
          <button>Home</button>
        </Link>

        <form
          id="form-cadastro"
          className="cadastrar-contato"
         // onSubmit={handleSubmit}
        >
          <label htmlFor="nome">
            Nome de contato
            <input
              id="nome"
              name="nome"
              type="text"
              pattern="[A-Za-z\s]+"
              title="O nome só deve conter letras. "
              maxLength="50"
              value={data.nome}
              //onKeyUp={onChangeHandle}
              required
            />
          </label>

          <label htmlFor="tel">
            Telefone
            <input
              id="tel"
              name="telefone"
              type="tel"
              minLength="7"
              maxLength="15"
              pattern="\d+"
              title="Colocar um número de telefone válido."
              value={data.telefone}
              //onKeyUp={onChangeHandle}
              required
            />
          </label>

          <label htmlFor="email">
            Email
            <input
              id="email"
              name="email"
              type="email"
              maxLength="30"
              value={data.email}
              //onKeyUp={onChangeHandle}
            />
          </label>
          <button type="submit">Enviar</button>
        </form>
        {/* {register && (
          <span id="thank-you-message" class="thank-you-message">
            Contato Atualizado!
          </span>
        )} */}
      </div>
      <Link to="/">
        <button>Cancel</button>
      </Link>
    </div>
  );
};
export default function EditarContato() {
  return <EditarContatoApp />;
}
