import React, { useState} from 'react';
import { FetchContatos } from './FetchContatos';



export default function ApagarContato({contactId}) {

    const [refresh, setRefresh] = useState(false);

    const deleteContact = async () => {
            fetch(`http://localhost:3000/contatos/${contactId}`, {
                method: 'DELETE',
                headers: {
                  'Content-Type': 'application/json',
                },
              })
              .catch((err)=>
            console.error('Api Request Delete Error: ', err));
        
          }

    function handleClick(){
        const apagarConfirmacao = window.confirm("¿Tem certeza de que deseja apagar este contato?");
        if (apagarConfirmacao === true){
            deleteContact();
            setRefresh(true);
        }
       
   }

  return (
    <div>
         <button onClick={handleClick}>Apagar</button>
    </div>
  )
}
