  return (
    <div>
        <Link to="/"><button>Home</button></Link>

      <form id="form-cadastro" className="cadastrar-contato">
        <label htmlFor="nome">
          Nome
          <input
            id="nome"
            name="nome"
            type="text"
            onKeyUp={onChangeHandle}
            required
          />
        </label>

        <label htmlFor="tel">
          Telefone
          <input id="tel" name="telefone" type="tel" onKeyUp={onChangeHandle} required />
        </label>

        <label htmlFor="email">
          Email
          <input id="email" name="email" type="email" onKeyUp={onChangeHandle} required />
        </label>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default function CadastrarContato() {
  return <CadastrarContatoWrap />;
}
